Exhibit.DataEdit.de1ete=function(itemId,ev){if(!Exhibit.DataEdit._invokeEventHandlers("onBeforeDelete",itemId)){return ;
}if(confirm("Do you really want to delete this item?\nItem: "+itemId)){var item=database._spo[itemId];
for(p in item){database.removeObjects(itemId,p);
}}else{return ;
}Exhibit.DataEdit._setLock(null);
database._listeners.fire("onAfterLoadingItems",[]);
Exhibit.DataEdit._invokeEventHandlers("onDelete",itemId);
};
Exhibit.DataEdit.clone=function(itemId,ev){if(!Exhibit.DataEdit._invokeEventHandlers("onBeforeClone",itemId)){return ;
}if(confirm("Do you really want to clone this item?\nItem: "+itemId)){var keyProp=(database.getObject(itemId,"id"))?"id":"label";
var _id=database.getObject(itemId,keyProp);
if(!_id||_id!=itemId){return ;
}if(_id.match(/\d+$/)){var m=_id.match(/(.*?)(\d*)$/);
var base=m[1];
var n=(m[2].length)?parseInt(m[2]):0;
do{n++;
}while(database.containsItem(base+n));
_id=base+n;
}else{_id=_id+"2";
}var srcItem=database._spo[itemId];
var destItem={};
for(var p in srcItem){var sa=srcItem[p];
if(typeof sa=="object"){var da=[];
for(var i=0;
i<sa.length;
i++){da.push(sa[i]);
}destItem[p]=da;
}else{destItem[p]=sa;
}}destItem[keyProp]=[_id];
var hType="onCloning";
for(var i=0;
i<Exhibit.DataEdit._lifeCycleEventHandlers_.length;
i++){var handler=Exhibit.DataEdit._lifeCycleEventHandlers_[i];
destItem=(handler[hType])?handler[hType](itemId,_id,destItem):destItem;
}database.loadData({items:[destItem]});
}else{return ;
}Exhibit.DataEdit._setLock(null);
database._listeners.fire("onAfterLoadingItems",[]);
Exhibit.DataEdit._invokeEventHandlers("onClone",itemId);
};
Exhibit.DataEdit.create=function(itemId,ev){if(!Exhibit.DataEdit._invokeEventHandlers("onBeforeCreate",itemId)){return ;
}var item={};
var srcItem=database._spo[itemId];
for(p in srcItem){var o=database.getObject(itemId,p);
if(typeof o=="number"){item[p]=0;
}else{if(typeof o=="boolean"){item[p]=false;
}else{if(typeof o=="string"){if(o.match(/^(\+|\-)?\d+(\.\d+)?$/)){item[p]=0;
}else{item[p]="";
}}else{item[p]="";
}}}}var type=database.getObject(itemId,"type");
item["type"]=(type)?type:"item";
var keyProp=(database.getObject(itemId,"id"))?"id":"label";
item[keyProp]="item"+(new Date().getTime());
var hType="onCreating";
for(var i=0;
i<Exhibit.DataEdit._lifeCycleEventHandlers_.length;
i++){var handler=Exhibit.DataEdit._lifeCycleEventHandlers_[i];
item=(handler[hType])?handler[hType](item[keyProp],item):item;
}database.loadData({items:[item]});
Exhibit.DataEdit._setLock(null);
database._listeners.fire("onAfterLoadingItems",[]);
Exhibit.DataEdit._invokeEventHandlers("onCreate",itemId);
};
Exhibit.DataEdit.save=function(itemId){var self=this;
var editor=Exhibit.DataEdit._editors_[itemId];
var fields=editor.getFields();
var item={};
for(var fieldId in fields){item[fieldId]=fields[fieldId].getValue();
}if(!Exhibit.DataEdit._invokeEventHandlers("onBeforeSave",itemId,item)){return ;
}for(var fieldId in fields){fields[fieldId].setError(false);
}Exhibit.DataEdit.onSaveErrors=[];
for(var fieldId in fields){var f=fields[fieldId];
var err=Exhibit.DataEdit._saveField(itemId,fieldId,f);
if(err){f.setError(true);
Exhibit.DataEdit._setStatusMessage(itemId,err);
return ;
}}var persistSuccess=Exhibit.DataEdit._invokeEventHandlers("onSave",itemId,item);
Exhibit.DataEdit._checkSaveFailure(itemId,persistSuccess);
};
Exhibit.DataEdit.onChange=function(itemId,fieldId){var editor=Exhibit.DataEdit._editors_[itemId];
var fields=editor.getFields();
var f=fields[fieldId];
var item={};
item[fieldId]=f.getValue();
if(!Exhibit.DataEdit._invokeEventHandlers("onBeforeSave",itemId,item)){return ;
}Exhibit.DataEdit.onSaveErrors=[];
var err=Exhibit.DataEdit._saveField(itemId,fieldId,f);
if(err){f.setError(true);
Exhibit.DataEdit._setStatusMessage(itemId,err);
}var persistSuccess=Exhibit.DataEdit._invokeEventHandlers("onSave",itemId,item);
Exhibit.DataEdit._checkSaveFailure(itemId,persistSuccess);
};
Exhibit.DataEdit._setLock=function(buttId){Exhibit.DataEdit._lock_=buttId;
for(var b in Exhibit.DataEdit.EDIT_MODE_REFDATA){var bRef=Exhibit.DataEdit.EDIT_MODE_REFDATA[b];
var matched=(b==buttId);
$("#"+b).each(function(){$("."+Exhibit.DataEdit.EDIT_BUTTON_SYMBOL,this).html(matched?'<span class="on">&#10004;</span>':'<span class="off">&#10006;</span>');
$("."+Exhibit.DataEdit.EDIT_BUTTON_TEXT,this).html(matched?'<span class="on">'+bRef.textOn+"</span>":'<span class="off">'+bRef.textOff+"</span>");
});
var unset=!matched?Exhibit.DataEdit.LENS_BUTTON_CSS_ON:Exhibit.DataEdit.LENS_ROLE_ACTIVATE_CSS_OFF;
var set=matched?Exhibit.DataEdit.LENS_BUTTON_CSS_ON:Exhibit.DataEdit.LENS_ROLE_ACTIVATE_CSS_OFF;
var role=bRef.customButton;
var filter=function(idx){return $(this).attr("ex:role")==role;
};
$("*").filter(filter).removeClass(unset).addClass(set);
}};
Exhibit.DataEdit._checkSaveFailure=function(itemId,persistSuccess){if(!persistSuccess){if(Exhibit.DataEdit.onSaveErrors.length>0){var s="";
for(var i=0;
i<Exhibit.DataEdit.onSaveErrors.length;
i++){s=s+Exhibit.DataEdit.onSaveErrors[i]+"\n";
}Exhibit.DataEdit._setStatusMessage(itemId,s,true);
}else{Exhibit.DataEdit._setStatusMessage(itemId,"Saved locally, persistence failed",true);
}}else{Exhibit.DataEdit._setStatusMessage(itemId,"Saved",false);
}};
Exhibit.DataEdit._setStatusMessage=function(itemId,mesg,urgent){var sel="#"+Exhibit.DataEdit.EDIT_STATUS_ID+"_"+itemId;
$(sel).html(mesg);
if(urgent&&$(sel).length==0){alert(mesg);
}};
Exhibit.DataEdit._saveField=function(itemId,fieldId,f){var val=f.getValue();
var validators=f._validators;
if(validators){var _vNames=validators.split(",");
for(var i=0;
i<_vNames.length;
i++){var err=null;
var v=_vNames[i];
var found=false;
var vl=v.toLowerCase();
if(Exhibit.DataEdit._internalValidators[vl]){err=Exhibit.DataEdit._internalValidators[vl](val);
found=true;
}if(!found&&(window[v])&&(typeof window[v]=="function")){err=window[v](val);
found=true;
}if(!found){}if(err!=null){return err;
}}}if(val!=undefined){database.removeObjects(itemId,fieldId);
if(val instanceof Array){Exhibit.DataEdit.log("Updating(array)",fieldId,val);
for(var j=0;
j<val.length;
j++){database.addStatement(itemId,fieldId,val[j]);
}}else{Exhibit.DataEdit.log("Updating(scalar)",fieldId,val);
database.addStatement(itemId,fieldId,val);
}}return null;
};
Exhibit.DataEdit.addEventHandler=function(h){if(h){Exhibit.DataEdit._lifeCycleEventHandlers_.push(h);
}};
Exhibit.DataEdit._invokeEventHandlers=function(type,id,item){var ret=true;
for(var i=0;
i<Exhibit.DataEdit._lifeCycleEventHandlers_.length;
i++){var handler=Exhibit.DataEdit._lifeCycleEventHandlers_[i];
if(handler[type]){ret=ret&&handler[type](id,item);
}}return ret;
};
Exhibit.DataEdit.validateIsNotEmpty=function(v){var err="Cannot be empty";
if(v==undefined){return err;
}else{if((typeof v=="string")&&(v.length==0)){return err;
}else{if((typeof v=="number")&&(v==0)){return err;
}else{if((typeof v=="object")&&(v instanceof Array)&&(v.length==0)){return err;
}else{return null;
}}}}};
Exhibit.DataEdit.validateIsURL=function(v){var err="Must be a web address";
if(v==undefined||(typeof v!="string")){return err;
}return(!v.match(/^http:\/\//))?err:null;
};
Exhibit.DataEdit._internalValidators={"notempty":Exhibit.DataEdit.validateIsNotEmpty,"isurl":Exhibit.DataEdit.validateIsURL};
Exhibit.DataEdit._setup_injectActivateButton=function(){if(Exhibit.DataEdit.X_DISABLE_MODE_BUTTONS){return ;
}var buttonHTML="";
var numCustomButtons=0;
for(var buttId in Exhibit.DataEdit.EDIT_MODE_REFDATA){var b=Exhibit.DataEdit.EDIT_MODE_REFDATA[buttId];
var cb=b.customButton;
var filter=function(idx){return($(this).attr("ex:role")==Exhibit.DataEdit.LENS_ROLE_BUTTON)&&($(this).attr("ex:type")==cb);
};
var l=$("*").filter(filter);
if(l.length>0){l.each(function(idx){try{var b=buttId;
$(this).click(function(){Exhibit.DataEdit.toggleMode(b);
}).addClass(Exhibit.DataEdit.LENS_BUTTON_CSS_OFF);
}catch(err){SimileAjax.Debug.warn(err);
}});
numCustomButtons++;
}else{buttonHTML+='<span id="'+buttId+'" class="'+Exhibit.DataEdit.EDIT_BUTTON+'"><a href="javascript:Exhibit.DataEdit.toggleMode(\''+buttId+'\');"><div><span class="'+Exhibit.DataEdit.EDIT_BUTTON_SYMBOL+'"><span class="off">&#10006;</span></span>&nbsp;<span class="'+Exhibit.DataEdit.EDIT_BUTTON_TEXT+'"><span class="off">'+b.textOff+"</span></span></div></a></span>";
}}if(numCustomButtons==0&&buttonHTML.length){buttonHTML='<div style="position:Fixed; float:Right; right:0.5em;  top:0.5em; z-index:1000;">'+buttonHTML+"</div>";
$("body").first().prepend(buttonHTML);
}};
$(document).ready(function(){if(!Exhibit.DataEdit._invokeEventHandlers("onBeforeInit")){return ;
}Exhibit.DataEdit._setup_injectActivateButton();
Exhibit.DataEdit._invokeEventHandlers("onInit");
});
