var tag=Exhibit.DataEdit.Editor._htmlTag("input",{"id":this._divId,"style":style,"class":"exhibitDataEditNumberField","type":"Text","value":this._format(this._value),"onchange":onChange},$(this._jqThis).get()[0],true);
